import { DashboardOutlined, LogoutOutlined } from '@ant-design/icons'
import { Card, Image, Layout, Menu, Spin, message } from 'antd'
import { useEffect, useState } from 'react'
import { Link } from 'react-router'
import TooltipButton from '../components/button/toolltip'
import { productType } from '../components/product/types'
import { base_url } from '../constants/env'
import axiosInstance from '../utils/axios'

const { Header, Content } = Layout

export default function Home() {
  const [data, setData] = useState<productType[]>([])
  const [loading, setLoading] = useState(false)
  const locale = JSON.parse(String(localStorage.getItem('user')))

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true)
      try {
        const response = await axiosInstance.get(`${base_url}/api/v1/public/products`)
        setData(response.data?.data)
      } catch (error) {
        message.error('Gagal memuat data produk')
      } finally {
        setLoading(false)
      }
    }
    fetchData()
  }, [])

  return (
    <Layout>
      <Header style={{ backgroundColor: '#fff', padding: '0 24px' }}>
        <div className="flex justify-between items-center w-full">
          <Link to="/">
            <h1 className='text-xl font-bold'>E-KASIR</h1>
          </Link>
          <Menu mode="vertical">
            <Menu.Item key="signin">
              <Link to="/signin">
                <TooltipButton
                  title={locale ? 'Dashboard' : 'Sign In'}
                  text={locale ? 'Dashboard' : 'Sign In'}
                  textSize="xs"
                  icon={locale ? <DashboardOutlined /> : <LogoutOutlined />}
                  type="default"
                  shape="circle"
                  size="small"
                />
              </Link>
            </Menu.Item>
          </Menu>
        </div>
      </Header>
      <Content className="p-4">
        <Spin spinning={loading}>
          <div className='flex flex-col justify-center w-full items-center'>
            <h1>KATALOG PRODUK</h1>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
              {data.map((product) => (
                <Card key={product.id} className="border border-gray-300 rounded-lg shadow-md shadow-blue-500">
                  <div className='flex justify-center items-center'>
                    <Image
                      className="outline outline-1 outline-gray-400 object-cover"
                      width={200}
                      height={200}
                      src={product.imageUrl}
                      fallback="data:image/pngbase64,"
                    />
                  </div>
                  <div className="p-4">
                    <h2 className="text-lg font-bold">{product.name}</h2>
                    <p className="text-gray-600"><span className='text-blue-500'>Kode Produk</span> : {product.code}</p>
                    <p className="text-gray-600"><span className='text-blue-500'>Kategori</span> : {product.category?.name || '-'}</p>
                    <p className="text-gray-600"><span className='text-blue-500'>Harga</span> : Rp {product.price}</p>
                  </div>
                </Card>
              ))}
            </div>
          </div>
        </Spin>
      </Content>
    </Layout>
  )
}
